main = do

    -- Bubble Sort --
    start <- getCurrentTime
    evaluate $ bubbleSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Bubble Sort:  " ++ show dif)
    
    -- Insertion Sort --
    start <- getCurrentTime
    evaluate $ insertionSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Insertion Sort:  " ++ show dif)
    
    -- Selection Sort --
    start <- getCurrentTime
    evaluate $ selectionSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Selection Sort:  " ++ show dif)
    
    -- Merge Sort --
    start <- getCurrentTime
    evaluate $ mergeSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Merge Sort:      " ++ show dif)
    
    -- Quick Sort --
    start <- getCurrentTime
    evaluate $ quickSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Quick Sort:      " ++ show dif)
    
    -- Counting Sort --
    start <- getCurrentTime
    evaluate $ countingSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Counting Sort:   " ++ show dif)
    
    -- Max-Heap Sort --
    start <- getCurrentTime
    evaluate $ heapSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Heap Sort:       " ++ show dif)
    
    -- Bucket Sort (10 buckets) --
    start <- getCurrentTime
    evaluate $ bucketSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Bucket Sort:     " ++ show dif)
    
    -- Radix Sort (LSB) --
    start <- getCurrentTime
    evaluate $ radixSort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Radix Sort:      " ++ show dif)
    
    -- Default Haskell Sorting --
    start <- getCurrentTime
    evaluate $ sort array
    end <- getCurrentTime
    let dif = diffUTCTime end start
    print ("Default Haskell: " ++ show dif)
    
